import { createSignal } from "solid-js"

import {
  NumberField,
  NumberFieldDecrementTrigger,
  NumberFieldErrorMessage,
  NumberFieldGroup,
  NumberFieldIncrementTrigger,
  NumberFieldInput,
  NumberFieldLabel,
} from "@repo/tailwindcss/ui/v4/number-field"

const NumberFieldErrorDemo = () => {
  const [value, setValue] = createSignal(1000)

  return (
    <NumberField
      formatOptions={{
        style: "currency",
        currency: "VND",
      }}
      rawValue={value()}
      onRawValueChange={setValue}
      validationState={value() >= 1000 ? "valid" : "invalid"}
      minValue={0}
    >
      <NumberFieldLabel>Payment</NumberFieldLabel>
      <NumberFieldGroup>
        <NumberFieldInput />
        <NumberFieldDecrementTrigger aria-label="Decrement" />
        <NumberFieldIncrementTrigger aria-label="Increment" />
      </NumberFieldGroup>
      <NumberFieldErrorMessage>
        Min â‚«1000 to send payment
      </NumberFieldErrorMessage>
    </NumberField>
  )
}

export default NumberFieldErrorDemo
